import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
//import App from './App';
import reportWebVitals from './reportWebVitals';



class Calculator extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      input: "5318008",
      output: "test of Output"
    };
    this.updateText = this.updateText.bind(this);
  }

  updateText(e) {
    this.setState(
      e.target.value==="clear"
        ? {input: 0}
        : {input: this.state.input + e.target.value}
    );
  }

  render() {
    return (
      <div className="calcBackground">
        <OutputScreen className="output" value={this.state.output} />
        <InputScreen className="input" value={this.state.input} />
        <div id="buttonBackground">
          <button id="clear" value="clear" onClick={this.updateText}>AC</button>
          <button id="divide" value="/" className="operator" onClick={this.updateText}>/</button>
          <button id="multiply" value="*" className="operator">*</button>
          <button id="subtract" value="-" className="operator">-</button>
          <button id="seven" value="7">7</button>
          <button id="eight" value="8">8</button>
          <button id="nine" value="9">9</button>
          <button id="four" value="4">4</button>
          <button id="five" value="5">5</button>
          <button id="six" value="6">6</button>
          <button id="plus" value="+" className="operator">+</button>
          <button id="one" value="1">1</button>
          <button id="two" value="2">2</button>
          <button id="three" value="3">3</button>
          <button id="zero" value="0">0</button>
          <button id="decimal" value=".">.</button>
          <button id="enter" value="Enter">Enter</button>
        </div>
      </div>
    )
  }

}

class InputScreen extends React.Component {
  render() {
    return (
      <div className={this.props.className} value={this.props.value}>{this.props.value}</div>
    );
  }
}

class OutputScreen extends React.Component {
  render() {
    return (
      <div className={this.props.className} value={this.props.value}>{this.props.value}</div>
    );
  }
}

ReactDOM.render(
  <React.StrictMode>
    <Calculator />
  </React.StrictMode>,
  document.getElementById('root')
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
