import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
//import App from './App';
import reportWebVitals from './reportWebVitals';



class Calculator extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      content: "5318008"
    };
    this.updateText = this.updateText.bind(this);
  }

  updateText(e) {
    this.setState(
      {content: e.target.value}
    );
    console.log(this.state.content);
  }

  render() {
    return (
      <div class="calc-background">
        <OutputScreen class="output" />
        <InputScreen class="input" value={this.state.content}/>
        <div id="button-background">
          <button id="clear" value="hello" onClick={this.updateText}>AC</button>
          <button id="divide" value="/" class="operator">/</button>
          <button id="multiply" value="*" class="operator">*</button>
          <button id="subtract" value="-" class="operator">-</button>
          <button id="seven" value="7">7</button>
          <button id="eight" value="8">8</button>
          <button id="nine" value="9">9</button>
          <button id="four" value="4">4</button>
          <button id="five" value="5">5</button>
          <button id="six" value="6">6</button>
          <button id="plus" value="+" class="operator">+</button>
          <button id="one" value="1">1</button>
          <button id="two" value="2">2</button>
          <button id="three" value="3">3</button>
          <button id="zero" value="0">0</button>
          <button id="decimal" value=".">.</button>
          <button id="enter" value="Enter">Enter</button>
        </div>
      </div>
    )
  }

}

class InputScreen extends React.Component {
  constructor(props) {
    super(props);
    this.state = { value: props.value };
  }
  render() {
    return (
      <div class={this.props.class} value={this.state.value}>{this.state.value}</div>
    );
    console.log(this.state.value);
  }
}

class OutputScreen extends React.Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  render() {
    return (
      <div class={this.props.class}>This is a test of output
      </div>
    );
  }
}

ReactDOM.render(
  <React.StrictMode>
    <Calculator />
  </React.StrictMode>,
  document.getElementById('root')
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
