import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
//import App from './App';
import reportWebVitals from './reportWebVitals';


function evaluate(input) {
  if (input !== 0) {
    if(/[^0-9]/.test(input.slice(-1))){
      input = input.slice(0, -1);
    }
    return Function('return ' + input)();
  }
}

class Calculator extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      input: "0",
      output: "0",
      currentChar: ""
    };
    this.updateText = this.updateText.bind(this);
  }

  updateText(e) {
    // run evaluate if enter key is pressed
    if (e.target.value === "enter") {
      this.setState({ output: evaluate(this.state.input) });
    }
    // clear input, output, currentChar if AC key is pressed
    else if (e.target.value === "clear") {
      this.setState({ input: 0, output: 0, currentChar: "" });
    }
    // if input screen is clear, allow only numbers or '-' and '.'
    else if (this.state.input == 0) {
      if ((/[0-9]|-|\./.test(e.target.value))) {
        this.setState({
          input: e.target.value,
          currentChar: e.target.value
        });
      }
    }
    else if (/\*|\/|-|\.|\+/.test(e.target.value)) {
      if (e.target.value !== this.state.currentChar && /[^0-9]/.test(e.target.value)) {
        this.setState({
          input: this.state.input + e.target.value,
          currentChar: e.target.value
        });
      }
    }
    else {
      this.setState({
        input: this.state.input + e.target.value,
        currentChar: e.target.value
      });
    }
  }


  render() {
    return (
      <div className="calcBackground">
        <OutputScreen className="output" value={this.state.output} />
        <InputScreen className="input" value={this.state.input} />
        <div id="buttonBackground">
          <button id="clear" value="clear" onClick={this.updateText}>AC</button>
          <button id="divide" value="/" className="operator" onClick={this.updateText}>/</button>
          <button id="multiply" value="*" className="operator" onClick={this.updateText}>*</button>
          <button id="subtract" value="-" className="operator" onClick={this.updateText}>-</button>
          <button id="seven" value="7" onClick={this.updateText}>7</button>
          <button id="eight" value="8" onClick={this.updateText}>8</button>
          <button id="nine" value="9" onClick={this.updateText}>9</button>
          <button id="four" value="4" onClick={this.updateText}>4</button>
          <button id="five" value="5" onClick={this.updateText}>5</button>
          <button id="six" value="6" onClick={this.updateText}>6</button>
          <button id="plus" value="+" className="operator" onClick={this.updateText}>+</button>
          <button id="one" value="1" onClick={this.updateText}>1</button>
          <button id="two" value="2" onClick={this.updateText}>2</button>
          <button id="three" value="3" onClick={this.updateText}>3</button>
          <button id="zero" value="0" onClick={this.updateText}>0</button>
          <button id="decimal" value="." onClick={this.updateText}>.</button>
          <button id="enter" value="enter" onClick={this.updateText}>Enter</button>
        </div>
      </div>
    )
  }

}

class InputScreen extends React.Component {
  render() {
    return (
      <div className={this.props.className} value={this.props.value}>{this.props.value}</div>
    );
  }
}

class OutputScreen extends React.Component {
  render() {
    return (
      <div className={this.props.className} value={this.props.value}>{this.props.value}</div>
    );
  }
}

ReactDOM.render(
  <React.StrictMode>
    <Calculator />
  </React.StrictMode>,
  document.getElementById('root')
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
